package app.model;import app.setting.BotConfig;import org.telegram.telegrambots.bots.TelegramLongPollingBot;import org.telegram.telegrambots.meta.api.objects.Update;import org.telegram.telegrambots.meta.exceptions.TelegramApiException;import java.io.IOException;public class Bot extends TelegramLongPollingBot {    private final String botName = BotConfig.BOT_NAME.getValue();    private final String botToken = BotConfig.BOT_TOKEN.getValue();    private Processor processor;    @Override    public void onUpdateReceived(Update update) {        try {            execute(processor.processorStart(update));        }catch (TelegramApiException e){            System.out.println("Виникла невідома помилка підчас обробки");        } catch (IOException e) {            throw new RuntimeException(e);        }    }    @Override    public String getBotUsername() {        return botName;    }    @Override    public void onRegister() {        super.onRegister();        this.processor = Processor.getProcessor();    }    @Override    public String getBotToken(){        return botToken;    }}